import React, { useState } from 'react';
import styled from 'styled-components';
import StaffDisplay from './components/StaffDisplay';
import CustomerDisplay from './components/CustomerDisplay';

const AppContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  padding: 20px;
`;

const DisplaySelector = styled.div`
  position: fixed;
  top: 20px;
  right: 20px;
  z-index: 1000;
`;

const SelectorButton = styled.button`
  background: ${props => props.active ? '#4CAF50' : '#2196F3'};
  color: white;
  border: none;
  padding: 10px 20px;
  margin: 0 5px;
  border-radius: 5px;
  cursor: pointer;
  font-size: 14px;
  font-weight: bold;
  transition: all 0.3s ease;

  &:hover {
    background: ${props => props.active ? '#45a049' : '#1976D2'};
    transform: translateY(-2px);
  }
`;

const Title = styled.h1`
  text-align: center;
  color: white;
  margin-bottom: 30px;
  font-size: 2.5rem;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
`;

function App() {
  const [displayMode, setDisplayMode] = useState('staff'); // 'staff' 或 'customer'

  return (
    <AppContainer>
      <DisplaySelector>
        <SelectorButton 
          active={displayMode === 'staff'} 
          onClick={() => setDisplayMode('staff')}
        >
          營業員畫面
        </SelectorButton>
        <SelectorButton 
          active={displayMode === 'customer'} 
          onClick={() => setDisplayMode('customer')}
        >
          客戶畫面
        </SelectorButton>
      </DisplaySelector>

      <Title>POS 雙顯示器系統</Title>

      {displayMode === 'staff' ? <StaffDisplay /> : <CustomerDisplay />}
    </AppContainer>
  );
}

export default App;
