import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const CustomerContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  background: white;
  border-radius: 15px;
  box-shadow: 0 10px 30px rgba(0,0,0,0.2);
  overflow: hidden;
`;

const Header = styled.div`
  background: linear-gradient(135deg, #e74c3c 0%, #c0392b 100%);
  color: white;
  padding: 30px;
  text-align: center;
`;

const HeaderTitle = styled.h2`
  margin: 0;
  font-size: 2.5rem;
  font-weight: bold;
`;

const Subtitle = styled.p`
  margin: 10px 0 0 0;
  font-size: 1.2rem;
  opacity: 0.9;
`;

const Content = styled.div`
  padding: 40px;
  text-align: center;
`;

const WelcomeMessage = styled.div`
  font-size: 1.5rem;
  color: #7f8c8d;
  margin-bottom: 40px;
`;

const OrderDisplay = styled.div`
  background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
  border-radius: 15px;
  padding: 30px;
  margin-bottom: 30px;
  border: 3px solid #3498db;
  box-shadow: 0 5px 15px rgba(0,0,0,0.1);
`;

const OrderHeader = styled.div`
  margin-bottom: 25px;
`;

const OrderNumber = styled.div`
  font-size: 2.5rem;
  font-weight: bold;
  color: #2c3e50;
  margin-bottom: 10px;
`;

const CustomerName = styled.div`
  font-size: 1.3rem;
  color: #7f8c8d;
  margin-bottom: 5px;
`;

const OrderDate = styled.div`
  font-size: 1rem;
  color: #95a5a6;
`;

const OrderItems = styled.div`
  margin-top: 25px;
`;

const ItemRow = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 15px 0;
  border-bottom: 1px solid #ddd;
  font-size: 1.2rem;

  &:last-child {
    border-bottom: none;
  }
`;

const ItemName = styled.span`
  font-weight: 500;
  color: #2c3e50;
`;

const ItemPrice = styled.span`
  color: #e74c3c;
  font-weight: bold;
`;

const TotalSection = styled.div`
  margin-top: 25px;
  padding-top: 20px;
  border-top: 3px solid #3498db;
`;

const TotalAmount = styled.div`
  font-size: 2rem;
  font-weight: bold;
  color: #2c3e50;
`;

const StatusMessage = styled.div`
  background: #3498db;
  color: white;
  padding: 15px;
  border-radius: 10px;
  font-size: 1.1rem;
  margin-bottom: 20px;
`;

const NoOrderMessage = styled.div`
  font-size: 1.5rem;
  color: #95a5a6;
  text-align: center;
  padding: 60px 20px;
`;

const ConnectionStatus = styled.div`
  position: fixed;
  top: 20px;
  left: 20px;
  background: ${props => props.connected ? '#27ae60' : '#e74c3c'};
  color: white;
  padding: 8px 15px;
  border-radius: 20px;
  font-size: 12px;
  font-weight: bold;
  z-index: 1000;
`;

const DebugInfo = styled.div`
  position: fixed;
  bottom: 20px;
  left: 20px;
  background: rgba(0,0,0,0.8);
  color: white;
  padding: 10px;
  border-radius: 5px;
  font-size: 12px;
  z-index: 1000;
`;

function CustomerDisplay() {
  const [currentOrder, setCurrentOrder] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [statusMessage, setStatusMessage] = useState('');
  const [debugInfo, setDebugInfo] = useState('初始化中...');

  useEffect(() => {
    let broadcastChannel = null;
    let localStorageInterval = null;

    const initializeCommunication = () => {
      // 嘗試使用 BroadcastChannel
      if ('BroadcastChannel' in window) {
        try {
          broadcastChannel = new BroadcastChannel('pos_channel');
          setDebugInfo('使用 BroadcastChannel');

          broadcastChannel.onmessage = (event) => {
            console.log('收到 BroadcastChannel 訊息:', event.data);
            setDebugInfo(`收到訊息: ${event.data.type}`);

            const { type, order } = event.data;

            if (type === 'NEW_ORDER') {
              setCurrentOrder(order);
              setStatusMessage(`新訂單已建立: ${order.number}`);
              setTimeout(() => setStatusMessage(''), 3000);
            } else if (type === 'SELECT_ORDER') {
              setCurrentOrder(order);
              setStatusMessage(`已選擇訂單: ${order.number}`);
              setTimeout(() => setStatusMessage(''), 3000);
            }
          };

          setIsConnected(true);
          setDebugInfo('BroadcastChannel 已連接');

        } catch (error) {
          console.error('BroadcastChannel 初始化失敗:', error);
          setDebugInfo('BroadcastChannel 失敗，使用 localStorage');
          initializeLocalStorageFallback();
        }
      } else {
        console.warn('BroadcastChannel 不支援，使用 localStorage 備用方案');
        setDebugInfo('BroadcastChannel 不支援，使用 localStorage');
        initializeLocalStorageFallback();
      }
    };

    const initializeLocalStorageFallback = () => {
      // 備用方案：使用 localStorage
      localStorageInterval = setInterval(() => {
        try {
          const storedOrder = localStorage.getItem('pos_currentOrder');
          if (storedOrder) {
            const order = JSON.parse(storedOrder);
            setCurrentOrder(order);
            setDebugInfo(`從 localStorage 讀取訂單: ${order.number}`);
            localStorage.removeItem('pos_currentOrder');
          }
        } catch (error) {
          console.error('localStorage 讀取失敗:', error);
          setDebugInfo('localStorage 讀取失敗');
        }
      }, 1000);

      setIsConnected(true);
    };

    initializeCommunication();

    // 清理函數
    return () => {
      if (broadcastChannel) {
        broadcastChannel.close();
      }
      if (localStorageInterval) {
        clearInterval(localStorageInterval);
      }
    };
  }, []);

  const formatDate = () => {
    const now = new Date();
    return now.toLocaleString('zh-TW', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <CustomerContainer>
      <ConnectionStatus connected={isConnected}>
        {isConnected ? '已連接' : '未連接'}
      </ConnectionStatus>

      <DebugInfo>{debugInfo}</DebugInfo>

      <Header>
        <HeaderTitle>客戶顯示器</HeaderTitle>
        <Subtitle>歡迎光臨</Subtitle>
      </Header>

      <Content>
        {statusMessage && (
          <StatusMessage>{statusMessage}</StatusMessage>
        )}

        {currentOrder ? (
          <OrderDisplay>
            <OrderHeader>
              <OrderNumber>{currentOrder.number}</OrderNumber>
              <CustomerName>{currentOrder.customerName}</CustomerName>
              <OrderDate>{formatDate()}</OrderDate>
            </OrderHeader>

            <OrderItems>
              {currentOrder.items.map((item, index) => (
                <ItemRow key={index}>
                  <ItemName>{item}</ItemName>
                  <ItemPrice>NT$ {getItemPrice(item)}</ItemPrice>
                </ItemRow>
              ))}
            </OrderItems>

            <TotalSection>
              <TotalAmount>總計: NT$ {currentOrder.total}</TotalAmount>
            </TotalSection>
          </OrderDisplay>
        ) : (
          <NoOrderMessage>
            等待訂單中...<br/>
            請稍候
          </NoOrderMessage>
        )}
      </Content>
    </CustomerContainer>
  );
}

// 輔助函數：根據品項名稱獲取價格
function getItemPrice(itemName) {
  const priceMap = {
    '美式咖啡': 60,
    '拿鐵咖啡': 80,
    '卡布奇諾': 75,
    '起司蛋糕': 90,
    '提拉米蘇': 120,
    '鬆餅': 80,
    '布朗尼': 70,
    '檸檬塔': 85,
    '熱茶': 50,
  };
  return priceMap[itemName] || 0;
}

export default CustomerDisplay;
